// src/App.js

import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";
import PredictionDisplay from "./components/PredictionDisplay";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundImage: `url('https://images.unsplash.com/photo-1566400832445-774c42a5d80b?ixlib=rb-4.0.3&auto=format&fit=crop&w=1950&q=80')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        padding: "30px",
        color: "white",
      }}
    >
      <h1 style={{ textAlign: "center", marginBottom: "20px" }}>
        üêâ 
        Game of Thrones Doodle Classifier
        üè∞
      </h1>
      <DrawingBoard onSubmit={handleSubmit} />
      <PredictionDisplay prediction={prediction} />
    </div>
  );
}

export default App;
