import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0a0a0a",
        color: "#fff",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "flex-start",
        overflowX: "hidden",
      }}
    >
      <h3
        style={{
          fontSize: "2rem",
          marginBottom: "20px",
          fontWeight: "bold",
          textAlign: "center",
          color: "#00ffff",
        }}
      >
        ðŸŽ¨ Sketch Portal ðŸŽ¨
      </h3>

      {/* Full Width Drawing Board */}
      <DrawingBoard onSubmit={handleSubmit} prediction={prediction} />
    </div>
  );
}

export default App;
