import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;
    setLoading(true);

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);
        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false);
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* üñåÔ∏è Flex container */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          marginBottom: "20px",
          flexWrap: "wrap",
          gap: "40px",
        }}
      >
        {/* üé® User Drawing */}
        <div>
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#00ffff"
            lazyRadius={1}
            canvasWidth={600}
            canvasHeight={400}
            backgroundColor="#111"
            style={{
              cursor: "crosshair",
              border: "2px solid #00ffff88",
              borderRadius: "15px",
              boxShadow: "0 0 20px #00ffff55",
            }}
          />
        </div>

        {/* ‚ú® Model Output */}
        <div
          style={{
            width: "600px",
            height: "400px",
            background: "#111",
            border: "2px solid #00ffff88",
            borderRadius: "15px",
            boxShadow: "0 0 20px #00ffff55",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          {decodedImageArray ? (
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          ) : (
            <p
              style={{
                color: "#00ffff",
                fontSize: "20px",
                fontWeight: "bold",
              }}
            >
              ‚ú® Model can draw
            </p>
          )}
        </div>
      </div>

      {/* üîò Buttons */}
      <div
        style={{
          marginTop: "20px",
          display: "flex",
          justifyContent: "center",
          gap: "20px",
        }}
      >
        <button
          onClick={handlePredict}
          disabled={loading}
          style={{
            padding: "12px 30px",
            fontSize: "16px",
            fontWeight: "bold",
            background: loading ? "#444" : "#00ffff",
            color: loading ? "#aaa" : "#0a0a0a",
            border: "none",
            borderRadius: "30px",
            cursor: loading ? "not-allowed" : "pointer",
            transition: "0.3s",
            boxShadow: "0 0 10px #00ffffaa",
          }}
        >
          {loading ? "Predicting..." : "Predict"}
        </button>

        <button
          onClick={handleClear}
          style={{
            padding: "12px 30px",
            fontSize: "16px",
            fontWeight: "bold",
            background: "#ff4081",
            color: "#fff",
            border: "none",
            borderRadius: "30px",
            cursor: "pointer",
            transition: "0.3s",
            boxShadow: "0 0 10px #ff4081aa",
          }}
        >
          Clear
        </button>
      </div>

      {/* üß† Prediction Text */}
      {prediction && (
        <h2
          style={{
            marginTop: "20px",
            fontSize: "20px",
            color: "#00ffff",
            textShadow: "0 0 5px #00ffff88",
          }}
        >
          Model Predicts: {prediction}
        </h2>
      )}
    </div>
  );
};

export default DrawingBoard;
