import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";

function App() {
  const [showAbout, setShowAbout] = useState(false);

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundColor: "#0a0a0a",
        color: "#fff",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "flex-start",
        overflowX: "hidden",
      }}
    >
      {/* üé® App Title */}
      {/* <h3
        style={{
          fontSize: "2rem",
          marginBottom: "20px",
          fontWeight: "bold",
          textAlign: "center",
          color: "#00ffff",
        }}
      >
        üé® Sketch Portal üé®
      </h3> */}

      {/* ‚ùì About Us Button */}
      <button
        onClick={() => setShowAbout(true)}
        style={{
          position: "absolute",
          top: "20px",
          right: "30px",
          background: "#00ffff",
          color: "#0a0a0a",
          fontWeight: "bold",
          fontSize: "20px",
          border: "none",
          borderRadius: "50%",
          width: "40px",
          height: "40px",
          cursor: "pointer",
          boxShadow: "0 0 15px #00ffff88",
          zIndex: 10,
        }}
      >
        ?
      </button>

      {/* üéØ Main Content */}
      <DrawingBoard />

      {/* üìú About Modal */}
      {showAbout && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0, 0, 0, 0.8)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 100,
          }}
          onClick={() => setShowAbout(false)}
        >
          <div
            style={{
              background: "#111",
              border: "2px solid #00ffff88",
              borderRadius: "20px",
              padding: "30px",
              color: "#00ffff",
              textAlign: "center",
              width: "90%",
              maxWidth: "500px",
              boxShadow: "0 0 20px #00ffff55",
            }}
            onClick={(e) => e.stopPropagation()} // prevent closing when clicking inside
          >
            <h2 style={{ marginBottom: "20px" }}>üåü About Us üåü</h2>
            <p style={{ fontSize: "16px", lineHeight: "1.6", color: "#ccc" }}>
              Welcome to the Sketch Portal! üé® Here you draw doodles and watch
              an AI model generate its interpretation! Built with love, neon
              dreams, and lots of coffee ‚òï‚ú®
            </p>
            <button
              onClick={() => setShowAbout(false)}
              style={{
                marginTop: "20px",
                padding: "10px 20px",
                fontSize: "16px",
                fontWeight: "bold",
                background: "#00ffff",
                color: "#0a0a0a",
                border: "none",
                borderRadius: "30px",
                cursor: "pointer",
                transition: "0.3s",
                boxShadow: "0 0 10px #00ffffaa",
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
