import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [isPredicting, setIsPredicting] = useState(false);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;

    setLoading(true);

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);

        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false);
        setIsPredicting(true); // Switch layout
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setIsPredicting(false);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* 🖌️ Top Heading */}
      <h1>Welcome to the Throne Game 🏰</h1>
      <h3 style={{ fontWeight: 300, marginBottom: "20px" }}>
        Only the worthy may draw.
      </h3>

      {/* 🖌️ Canvas Row */}
      <div
        style={{
          display: "flex",
          justifyContent: isPredicting ? "space-evenly" : "center",
          alignItems: "center",
          transition: "all 0.8s ease",
          marginBottom: "30px",
        }}
      >
        {/* 🎨 User Canvas */}
        <div
          style={{
            transform: isPredicting ? "scale(0.7)" : "scale(1)",
            transition: "all 0.8s ease",
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#fff"
            lazyRadius={1}
            canvasWidth={600}
            canvasHeight={400}
            backgroundColor="#000"
            style={{
              cursor: "crosshair",
              border: "2px solid gray",
              borderRadius: "10px",
            }}
          />
        </div>

        {/* 🎯 Model Canvas appears after prediction */}
        {isPredicting && decodedImageArray && (
          <div
            style={{
              opacity: isPredicting ? 1 : 0,
              transform: isPredicting ? "translateX(0px)" : "translateX(200px)",
              transition: "all 0.8s ease",
            }}
          >
            <h3>Look what the model can do! ✨</h3>
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          </div>
        )}
      </div>

      {/* 🔘 Predict and Clear Buttons */}
      <div style={{ marginTop: "20px" }}>
        {!isPredicting && (
          <>
            <button
              onClick={handlePredict}
              disabled={loading}
              style={{
                padding: "10px 20px",
                fontSize: "16px",
                backgroundColor: loading ? "#aaa" : "#B9540C",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                cursor: loading ? "not-allowed" : "pointer",
                marginRight: "10px",
                transition: "background 0.3s",
              }}
            >
              {loading ? "Predicting..." : "Predict"}
            </button>
            <button
              onClick={handleClear}
              style={{
                padding: "10px 20px",
                fontSize: "16px",
                backgroundColor: "#C38D5F",
                color: "#fff",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer",
                transition: "background 0.3s",
              }}
            >
              Clear
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default DrawingBoard;
