import React, { useEffect, useRef } from "react";

const GeneratedSketchAnimation = ({ decodedImageArray }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!decodedImageArray) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    // Resize Canvas to match 280x280 for nice view (not just 28x28 tiny)
    canvas.width = 280;
    canvas.height = 280;

    // Scale pixels
    const scale = 10; // 28x10 = 280

    // Flatten pixel positions and their brightness
    let pixels = [];
    for (let y = 0; y < 28; y++) {
      for (let x = 0; x < 28; x++) {
        const brightness = decodedImageArray[y][x]; // Should be 0-1 float
        if (brightness > 0.2) {
          // only keep visible pixels
          pixels.push({ x, y, brightness });
        }
      }
    }

    // Shuffle pixels a little to look more natural
    pixels = pixels.sort(() => Math.random() - 0.5);

    let pixelIndex = 0;
    const totalPixels = pixels.length;

    function drawNextPixels() {
      const pixelsPerFrame = 5; // How many pixels to draw each frame

      for (let i = 0; i < pixelsPerFrame; i++) {
        if (pixelIndex >= totalPixels) return; // All pixels drawn

        const { x, y, brightness } = pixels[pixelIndex];

        ctx.fillStyle = `rgb(${brightness * 255}, ${brightness * 255}, ${
          brightness * 255
        })`;
        ctx.fillRect(x * scale, y * scale, scale, scale);

        pixelIndex++;
      }

      if (pixelIndex < totalPixels) {
        requestAnimationFrame(drawNextPixels);
      }
    }

    // Clear canvas first
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Start animation
    drawNextPixels();
  }, [decodedImageArray]);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h3>Generated Sketch (Animated)</h3>
      <canvas
        ref={canvasRef}
        style={{ border: "2px solid gray", borderRadius: "8px" }}
      ></canvas>
    </div>
  );
};

export default GeneratedSketchAnimation;
