import React, { useEffect, useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const containerRef = useRef(null); // NEW: Ref for container
  const [canvasSize, setCanvasSize] = useState({ width: 600, height: 400 }); // Default size
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [loading, setLoading] = useState(false);

  // üìè Handle resizing
  useEffect(() => {
    if (containerRef.current) {
      const resizeCanvas = () => {
        const { clientWidth, clientHeight } = containerRef.current;
        setCanvasSize({
          width: clientWidth - 40, // Padding margins
          height: clientHeight - 100, // Space for buttons
        });
      };

      resizeCanvas();
      window.addEventListener("resize", resizeCanvas);
      return () => window.removeEventListener("resize", resizeCanvas);
    }
  }, []);

  // üöÄ Predict
  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;
    setLoading(true);

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);
        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false);
      }
    });
  };

  // üé® Fetch generated model output
  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  // üßπ Clear all
  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setLoading(false);
  };

  // üîÅ Generate next version
  const handleGenerateNextImage = () => {
    if (prediction) {
      fetchGeneratedDecodedImage(prediction);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        height: "calc(100vh - 100px)", // Full viewport height minus a little for header
        width: "100%",
        justifyContent: "space-between",
        gap: "20px",
        padding: "10px",
      }}
    >
      {/* üé® Left Side - Drawing */}
      <div
        ref={containerRef}
        style={{
          flex: 1,
          background: "#111",
          border: "2px solid #00ffff88",
          borderRadius: "20px",
          boxShadow: "0 0 20px #00ffff55",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: "20px",
          minHeight: "0",
        }}
      >
        {/* üîò Buttons */}
        {/* üîò Prediction + Buttons */}
        <div
          style={{
            marginBottom: "10px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "20px",
            flexWrap: "wrap",
          }}
        >
          <div
            style={{
              fontSize: "18px",
              fontWeight: "bold",
              color: "#00ffff",
              textShadow: "0 0 8px #00ffffaa",
            }}
          >
            Prediction:{" "}
            {prediction
              ? prediction.charAt(0).toUpperCase() +
                prediction.slice(1).toLowerCase()
              : "None"}
          </div>

          <button
            onClick={handlePredict}
            disabled={loading}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              background: loading ? "#444" : "#00ffff",
              color: loading ? "#aaa" : "#0a0a0a",
              border: "none",
              borderRadius: "30px",
              cursor: loading ? "not-allowed" : "pointer",
              transition: "0.3s",
              boxShadow: "0 0 10px #00ffffaa",
            }}
          >
            {loading ? "Predicting..." : "Predict"}
          </button>

          <button
            onClick={handleClear}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              background: "#ff4081",
              color: "#fff",
              border: "none",
              borderRadius: "30px",
              cursor: "pointer",
              transition: "0.3s",
              boxShadow: "0 0 10px #ff4081aa",
            }}
          >
            Clear
          </button>
        </div>

        {/* üñåÔ∏è Canvas */}
        <div
          style={{
            flex: 1,
            width: "100%",
            height: "100%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#00ffff"
            lazyRadius={1}
            canvasWidth={canvasSize.width}
            canvasHeight={canvasSize.height}
            backgroundColor="#111"
            // style={{
            //   border: "2px solid #00ffff88",
            //   borderRadius: "15px",
            //   boxShadow: "0 0 20px #00ffff55",
            // }}
          />
        </div>
      </div>

      {/* ‚ú® Right Side - Model Output */}
      <div
        style={{
          flex: 1,
          background: "#111",
          border: "2px solid #00ffff88",
          borderRadius: "20px",
          boxShadow: "0 0 20px #00ffff55",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          padding: "20px",
          minHeight: "0",
        }}
      >
        {/* Model Output */}
        <div
          style={{
            flex: 1,
            width: "100%",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {decodedImageArray ? (
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          ) : (
            <p
              style={{
                color: "#00ffff",
                fontSize: "20px",
                fontWeight: "bold",
              }}
            >
              ‚ú® Model can draw
            </p>
          )}
        </div>

        {/* üîÅ Generate Next Button */}
        {prediction && (
          <button
            onClick={handleGenerateNextImage}
            style={{
              marginTop: "20px",
              padding: "10px 20px",
              fontSize: "16px",
              fontWeight: "bold",
              background: "#00ffff",
              color: "#0a0a0a",
              border: "none",
              borderRadius: "30px",
              cursor: "pointer",
              transition: "0.3s",
              boxShadow: "0 0 10px #00ffffaa",
            }}
          >
            Generate Next Image
          </button>
        )}
      </div>
    </div>
  );
};

export default DrawingBoard;
