import React, { useRef, useState, useEffect } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation"; // üëà Import your animation component

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const intervalRef = useRef(null);

  const startPredicting = () => {
    if (intervalRef.current) return;
    intervalRef.current = setInterval(() => {
      handlePredict();
    }, 1500);
  };

  const stopPredicting = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
      intervalRef.current = null;
    }
  };

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;
    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);

        // After getting prediction, fetch the generated image array
        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json(); // Assuming server sends 2D array

      setDecodedImageArray(data.decoded_image); // Store decoded image array
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    stopPredicting();
    setIsDrawing(false);
  };

  const handleMouseDown = () => {
    setIsDrawing(true);
    startPredicting();
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
    stopPredicting();
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <div style={{ display: "flex", justifyContent: "center", gap: "50px" }}>
        {/* üñåÔ∏è User Drawing */}
        <div>
          <h3>Your Drawing</h3>
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#fff"
            lazyRadius={1}
            canvasWidth={400}
            canvasHeight={400}
            backgroundColor="#000"
            style={{
              cursor: "crosshair",
              border: "2px solid gray",
              borderRadius: "10px",
            }}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onTouchStart={handleMouseDown}
            onTouchEnd={handleMouseUp}
          />
        </div>

        {/* üéØ Generated Sketch Animation */}
        <div>
          <h3>Model's Generated Sketch</h3>
          {decodedImageArray ? (
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          ) : (
            <div
              style={{
                width: "280px",
                height: "280px",
                background: "#000",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                border: "2px solid gray",
                borderRadius: "10px",
              }}
            >
              <p style={{ color: "white" }}>Waiting for prediction...</p>
            </div>
          )}
        </div>
      </div>

      <div style={{ marginTop: "20px" }}>
        <button
          onClick={handleClear}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: "#C38D5F",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            transition: "background 0.3s",
          }}
        >
          Clear
        </button>
      </div>

      <h2 style={{ marginTop: "20px" }}>
        {prediction ? `Prediction: ${prediction}` : "Start Drawing!"}
      </h2>
    </div>
  );
};

export default DrawingBoard;
