import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [isPredicting, setIsPredicting] = useState(false);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;

    setLoading(true);

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);

        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false);
        setIsPredicting(true);
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setIsPredicting(false);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* üñåÔ∏è Flex container */}
      <div
        style={{
          display: "flex",
          justifyContent: isPredicting ? "space-around" : "center",
          alignItems: "center",
          transition: "all 0.8s ease",
          marginBottom: "20px",
        }}
      >
        {/* üé® User Drawing */}
        <div
          style={{
            transition: "all 0.8s ease",
            transform: isPredicting ? "scale(0.7)" : "scale(1)",
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#fff"
            lazyRadius={1}
            canvasWidth={600}
            canvasHeight={400}
            backgroundColor="#000"
            style={{
              cursor: "crosshair",
              border: "2px solid gray",
              borderRadius: "10px",
            }}
          />
        </div>

        {/* ‚ú® Model Side */}
        {isPredicting && (
          <div
            style={{
              opacity: 1,
              transform: "translateX(0)",
              transition: "all 0.8s ease",
            }}
          >
            {/* <h2 style={{ marginBottom: "20px" }}>
              Look at what the model can do! ‚ú®
            </h2> */}

            {/* Model Drawing or Message */}
            {decodedImageArray ? (
              <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
            ) : (
              <div
                style={{
                  width: "280px",
                  height: "280px",
                  background: "#000",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  border: "2px solid gray",
                  borderRadius: "10px",
                  color: "white",
                  fontSize: "18px",
                  fontWeight: "bold",
                }}
              >
                The model couldn't draw anything! üò¢
              </div>
            )}
          </div>
        )}
      </div>

      {/* üîò Buttons */}
      <div style={{ marginTop: "10px" }}>
        <button
          onClick={handlePredict}
          disabled={loading || isPredicting}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: loading ? "#aaa" : "#B9540C",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: loading ? "not-allowed" : "pointer",
            marginRight: "10px",
            transition: "background 0.3s",
            opacity: isPredicting ? 0.5 : 1, // fade Predict button after prediction
          }}
        >
          {loading ? "Predicting..." : isPredicting ? "Predict" : "Predict"}
        </button>
        <button
          onClick={handleClear}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: "#C38D5F",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            transition: "background 0.3s",
          }}
        >
          Clear
        </button>
      </div>
      <div> Prediction : </div>
    </div>
  );
};

export default DrawingBoard;
