// src/components/GeneratedSketch.js

import React, { useEffect, useRef } from "react";

const GeneratedSketch = ({ decodedImageArray }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!decodedImageArray) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const size = 280; // upscale to 280x280 for nice view
    const scale = size / 28; // 10

    canvas.width = size;
    canvas.height = size;

    // Clear background first
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, size, size);

    // Now threshold and draw
    const threshold = 0.5; // you can tweak if needed

    for (let y = 0; y < 28; y++) {
      for (let x = 0; x < 28; x++) {
        let brightness = decodedImageArray[y][x];

        // Apply threshold
        if (brightness > threshold) {
          ctx.fillStyle = "white";
          ctx.fillRect(x * scale, y * scale, scale, scale);
        }
      }
    }
  }, [decodedImageArray]);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h3>Generated Sketch</h3>
      <canvas
        ref={canvasRef}
        style={{
          border: "2px solid gray",
          borderRadius: "8px",
          backgroundColor: "black",
        }}
      />
    </div>
  );
};

export default GeneratedSketch;
