import React, { useEffect, useRef } from "react";

const GeneratedSketchImage = ({ decodedImageArray }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!decodedImageArray) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    canvas.width = 280;
    canvas.height = 280;

    // Clear
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    const scale = 10;

    for (let y = 0; y < 28; y++) {
      for (let x = 0; x < 28; x++) {
        const pixelValue = decodedImageArray[y][x];

        // 🛠️ Small tweak: Adjust contrast
        const brightness = Math.min(Math.max((pixelValue - 0.4) * 5.0, 0), 1);

        const color = Math.floor(brightness * 255);

        ctx.fillStyle = `rgb(${color}, ${color}, ${color})`;
        ctx.fillRect(x * scale, y * scale, scale, scale);
      }
    }
  }, [decodedImageArray]);

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h3>Generated Sketch</h3>
      <canvas
        ref={canvasRef}
        style={{
          border: "2px solid gray",
          borderRadius: "8px",
          backgroundColor: "black",
        }}
      ></canvas>
    </div>
  );
};

export default GeneratedSketchImage;
