import React, { useEffect, useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const containerRef = useRef(null); // Add a new ref for container
  const [canvasSize, setCanvasSize] = useState({ width: 600, height: 400 }); // Default size first

  useEffect(() => {
    if (containerRef.current) {
      const resizeCanvas = () => {
        const { clientWidth, clientHeight } = containerRef.current;
        setCanvasSize({ width: clientWidth, height: clientHeight });
      };

      resizeCanvas(); // Set once

      window.addEventListener("resize", resizeCanvas); // Update on window resize
      return () => window.removeEventListener("resize", resizeCanvas);
    }
  }, []);

  // (rest of your functions like handlePredict, handleClear etc.)

  return (
    <div
      style={{
        display: "flex",
        height: "calc(100vh - 100px)",
        width: "100%",
        justifyContent: "space-between",
        gap: "20px",
        padding: "10px",
      }}
    >
      {/* üé® Left Side */}
      <div
        ref={containerRef}
        style={{
          flex: 1,
          background: "#111",
          border: "2px solid #00ffff88",
          borderRadius: "20px",
          boxShadow: "0 0 20px #00ffff55",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: "20px",
          minHeight: "0", // Important fix for flexbox stretching
        }}
      >
        {/* üîò Buttons */}
        <div
          style={{
            marginBottom: "10px",
            display: "flex",
            gap: "10px",
            justifyContent: "center",
          }}
        >
          {/* Predict and Clear buttons here */}
        </div>

        {/* üñåÔ∏è Canvas */}
        <div
          style={{
            flex: 1,
            width: "100%",
            display: "flex",
            justifyContent: "center",
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#00ffff"
            lazyRadius={1}
            canvasWidth={canvasSize.width - 40} // little margin from sides
            canvasHeight={canvasSize.height - 120} // adjust height based on button row
            backgroundColor="#111"
            style={{
              border: "2px solid #00ffff88",
              borderRadius: "15px",
              boxShadow: "0 0 20px #00ffff55",
            }}
          />
        </div>
      </div>

      {/* ‚ú® Right Side */}
      {/* ... model side unchanged ... */}
    </div>
  );
};

export default DrawingBoard;
