import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation";

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [isPredicting, setIsPredicting] = useState(false);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;

    setLoading(true);

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);

        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false);
        setIsPredicting(true);
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setIsPredicting(false);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* 🖌️ Flex container */}
      <div
        style={{
          display: "flex",
          justifyContent: isPredicting ? "space-around" : "center",
          alignItems: "center",
          transition: "all 0.8s ease",
          marginBottom: "20px",
          flexWrap: "wrap",
        }}
      >
        {/* 🎨 User Drawing */}
        <div
          style={{
            transition: "all 0.8s ease",
            transform: isPredicting ? "scale(0.9)" : "scale(1)",
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushRadius={2}
            brushColor="#00ffff"
            lazyRadius={1}
            canvasWidth={600}
            canvasHeight={400}
            backgroundColor="#111"
            style={{
              cursor: "crosshair",
              border: "2px solid #00ffff88",
              borderRadius: "15px",
              boxShadow: "0 0 20px #00ffff55",
            }}
          />
        </div>

        {/* ✨ Model Side */}
        {isPredicting && (
          <div
            style={{
              opacity: 1,
              transform: "translateX(0)",
              transition: "all 0.8s ease",
              marginTop: "20px",
            }}
          >
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          </div>
        )}
      </div>

      {/* 🔘 Buttons */}
      <div
        style={{
          marginTop: "20px",
          display: "flex",
          justifyContent: "center",
          gap: "20px",
        }}
      >
        <button
          onClick={handlePredict}
          disabled={loading || isPredicting}
          style={{
            padding: "12px 30px",
            fontSize: "16px",
            fontWeight: "bold",
            background: loading ? "#444" : "#00ffff",
            color: loading ? "#aaa" : "#0a0a0a",
            border: "none",
            borderRadius: "30px",
            cursor: loading ? "not-allowed" : "pointer",
            transition: "0.3s",
            boxShadow: "0 0 10px #00ffffaa",
            opacity: isPredicting ? 0.7 : 1,
          }}
        >
          {loading ? "Predicting..." : "Predict"}
        </button>

        <button
          onClick={handleClear}
          style={{
            padding: "12px 30px",
            fontSize: "16px",
            fontWeight: "bold",
            background: "#ff4081",
            color: "#fff",
            border: "none",
            borderRadius: "30px",
            cursor: "pointer",
            transition: "0.3s",
            boxShadow: "0 0 10px #ff4081aa",
          }}
        >
          Clear
        </button>
      </div>

      {/* 🧠 Prediction Text */}
      {prediction && (
        <h2
          style={{
            marginTop: "20px",
            fontSize: "20px",
            color: "#00ffff",
            textShadow: "0 0 5px #00ffff88",
          }}
        >
          Model Predicts: {prediction}
        </h2>
      )}
    </div>
  );
};

export default DrawingBoard;
