// src/App.js

import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";
import PredictionDisplay from "./components/PredictionDisplay";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundImage: `url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQXMlm5spO19nD4QBRvM6j3ibqUo9avXjZ4Q&s')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        color: "white",
        padding: "30px",
        zIndex: 1,
        overflow: "hidden",
      }}
    >
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // dark overlay
          backdropFilter: "blur(1px)", // slight blur for even more blending
          zIndex: 0,
        }}
      />

      <div style={{ position: "relative", zIndex: 2 }}>
        {/* Your actual page content goes here */}

        <h1
          style={{
            textAlign: "center",
            marginBottom: "20px",
          }}
        >
          <h3>üêâ Welcome to the Throne Game üè∞</h3>
          <p>Only the worthy may draw.</p>
        </h1>
        <DrawingBoard onSubmit={handleSubmit} />
        <PredictionDisplay prediction={prediction} />
      </div>
    </div>
  );
}

export default App;
