import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";
import PredictionDisplay from "./components/PredictionDisplay";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundColor: "#0a0a0a",
        color: "#fff",
        padding: "20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        overflow: "hidden",
      }}
    >
      {/* Main Content */}
      <div
        style={{
          position: "relative",
          width: "100%",
          maxWidth: "1400px",
          padding: "20px",
          borderRadius: "20px",
          background: "linear-gradient(135deg, #1a1a1a 0%, #0d0d0d 100%)",
          boxShadow: "0 0 20px rgba(0, 255, 255, 0.2)",
        }}
      >
        <h3
          style={{
            fontSize: "1.5rem",
            marginBottom: "20px",
            fontWeight: "bold",
            textAlign: "center",
            color: "#00ffff",
          }}
        >
          ðŸŽ¨ Sketch Portal ðŸŽ¨
        </h3>

        {/* Split: User Drawing + Model Output */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            gap: "20px",
            flexWrap: "wrap",
          }}
        >
          {/* User Drawing Left */}
          <div
            style={{
              flex: 1,
              background: "#111",
              border: "2px solid #00ffff88",
              borderRadius: "20px",
              padding: "20px",
              minHeight: "80vh",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <h2 style={{ marginBottom: "10px", fontSize: "20px" }}>
              Draw Your Sketch
            </h2>
            <DrawingBoard onSubmit={handleSubmit} />
          </div>

          {/* Cute divider */}
          <div
            style={{
              fontSize: "30px",
              color: "#00ffff",
              opacity: 0.6,
            }}
          >
            --
          </div>

          {/* Model Output Right */}
          <div
            style={{
              flex: 1,
              background: "#111",
              border: "2px solid #00ffff88",
              borderRadius: "20px",
              padding: "20px",
              minHeight: "80vh",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <h2 style={{ marginBottom: "10px", fontSize: "20px" }}>
              Model's Magic âœ¨
            </h2>
            <PredictionDisplay prediction={prediction} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
