import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";
import GeneratedSketchAnimation from "./GeneratedSketchAnimation"; // Your animation component

const DrawingBoard = () => {
  const canvasRef = useRef(null);
  const [prediction, setPrediction] = useState("");
  const [decodedImageArray, setDecodedImageArray] = useState(null);
  const [isPredicting, setIsPredicting] = useState(false); // whether model predicted
  const [loading, setLoading] = useState(false); // predict button loading state

  const handlePredict = async () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvas.drawing;

    setLoading(true); // Start loading spinner

    canvas.toBlob(async (blob) => {
      const formData = new FormData();
      formData.append("file", blob, "doodle.png");

      try {
        const response = await fetch("http://127.0.0.1:8000/predict", {
          method: "POST",
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction);

        // Fetch the decoded/generated sketch
        fetchGeneratedDecodedImage(data.prediction);
      } catch (error) {
        console.error("Prediction error:", error);
      } finally {
        setLoading(false); // Stop loading
        setIsPredicting(true); // Switch UI after prediction
      }
    });
  };

  const fetchGeneratedDecodedImage = async (predictedClass) => {
    try {
      const response = await fetch(
        `http://127.0.0.1:8000/generate-decoded/${predictedClass}`
      );
      const data = await response.json();
      setDecodedImageArray(data.decoded_image);
    } catch (error) {
      console.error("Error fetching generated decoded image:", error);
    }
  };

  const handleClear = () => {
    canvasRef.current.clear();
    setPrediction("");
    setDecodedImageArray(null);
    setIsPredicting(false);
    setLoading(false);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      {/* üñåÔ∏è User Drawing */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          transition: "all 0.5s",
          transform: isPredicting ? "scale(0.7)" : "scale(1)",
          marginBottom: "20px",
        }}
      >
        <CanvasDraw
          ref={canvasRef}
          brushRadius={2}
          brushColor="#fff"
          lazyRadius={1}
          canvasWidth={isPredicting ? 300 : 600} // Shrink when predicting
          canvasHeight={isPredicting ? 300 : 400}
          backgroundColor="#000"
          style={{
            cursor: "crosshair",
            border: "2px solid gray",
            borderRadius: "10px",
          }}
        />
      </div>

      {/* Buttons */}
      {!isPredicting && (
        <div style={{ marginTop: "10px" }}>
          <button
            onClick={handlePredict}
            disabled={loading}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              backgroundColor: loading ? "#aaa" : "#B9540C",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: loading ? "not-allowed" : "pointer",
              marginRight: "10px",
              transition: "background 0.3s",
            }}
          >
            {loading ? "Predicting..." : "Predict"}
          </button>

          <button
            onClick={handleClear}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              backgroundColor: "#C38D5F",
              color: "#fff",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              transition: "background 0.3s",
            }}
          >
            Clear
          </button>
        </div>
      )}

      {/* ‚ú® After Prediction: Show Magic */}
      {isPredicting && (
        <>
          <h2 style={{ marginTop: "30px", marginBottom: "20px" }}>
            Look at what the model can do! ‚ú®
          </h2>

          {decodedImageArray ? (
            <GeneratedSketchAnimation decodedImageArray={decodedImageArray} />
          ) : (
            <div
              style={{
                width: "280px",
                height: "280px",
                background: "#000",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                border: "2px solid gray",
                borderRadius: "10px",
              }}
            >
              <p style={{ color: "white" }}>Generating sketch...</p>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default DrawingBoard;
