import React, { useRef, useState } from "react";
import CanvasDraw from "react-canvas-draw";

function App() {
  const canvasRef = useRef(null);
  const [modelOutput, setModelOutput] = useState(null);
  const [isPredicting, setIsPredicting] = useState(false);

  const handleClear = () => {
    canvasRef.current.clear();
  };

  const handlePredict = async () => {
    const canvasData = canvasRef.current.getDataURL("image/png");

    setIsPredicting(true);
    try {
      // Assume you send `canvasData` to backend and get back an image URL
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: JSON.stringify({ image: canvasData }),
        headers: { "Content-Type": "application/json" },
      });
      const data = await response.json();
      setModelOutput(data.generatedImageUrl); // This should be a URL or base64
    } catch (error) {
      console.error("Prediction failed:", error);
    } finally {
      setIsPredicting(false);
    }
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh", padding: "20px" }}>
      {/* Left side - Drawing */}
      <div
        style={{
          flex: 1,
          marginRight: "20px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h2>Draw Here üé®</h2>
        <CanvasDraw
          ref={canvasRef}
          canvasWidth={400}
          canvasHeight={400}
          brushColor="#000"
          brushRadius={2}
          lazyRadius={1}
          hideGrid={false}
        />
        <div style={{ marginTop: "10px" }}>
          <button onClick={handleClear} style={{ marginRight: "10px" }}>
            Clear
          </button>
          <button onClick={handlePredict}>
            {isPredicting ? "Predicting..." : "Predict"}
          </button>
        </div>
      </div>

      {/* Right side - Model output */}
      <div
        style={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <h2>Model Output üñºÔ∏è</h2>
        <div
          style={{
            width: 400,
            height: 400,
            border: "2px dashed #ccc",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            background: "#f9f9f9",
          }}
        >
          {modelOutput ? (
            <img
              src={modelOutput}
              alt="Model Output"
              style={{ maxWidth: "100%", maxHeight: "100%" }}
            />
          ) : (
            <p>No prediction yet</p>
          )}
        </div>
        <div style={{ marginTop: "10px" }}>
          <button onClick={handlePredict} disabled={isPredicting}>
            {isPredicting ? "Predicting..." : "Predict Again"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
