// src/App.js

import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";
import PredictionDisplay from "./components/PredictionDisplay";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundImage: `url('https://images.unsplash.com/photo-1515021863624-9b325c51faab?w=900&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Nnx8Z2FtZSUyMG9mJTIwdGhyb25lc3xlbnwwfHwwfHx8MA%3D%3D')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        color: "white",
        padding: "30px",
        zIndex: 1,
        overflow: "hidden",
      }}
    >
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // dark overlay
          backdropFilter: "blur(3px)", // slight blur for even more blending
          zIndex: 0,
        }}
      />

      <div style={{ position: "relative", zIndex: 2 }}>
        {/* Your actual page content goes here */}
        <h1>Welcome to the Throne Game</h1>
        <p>Only the worthy may sit.</p>

        <h1
          style={{
            textAlign: "center",
            marginBottom: "20px",
            color: "black",
          }}
        >
          üêâ Doodle Classifier üè∞
        </h1>
        <DrawingBoard onSubmit={handleSubmit} />
        <PredictionDisplay prediction={prediction} />
      </div>
    </div>
  );
}

export default App;
