import React, { useState } from "react";
import DrawingBoard from "./components/DrawingBoard";
import PredictionDisplay from "./components/PredictionDisplay";

function App() {
  const [prediction, setPrediction] = useState("");

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/predict", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setPrediction(data.prediction);
    } catch (error) {
      console.error("Error predicting doodle:", error);
    }
  };

  return (
    <div
      style={{
        position: "relative",
        minHeight: "100vh",
        backgroundImage: `url('https://images.unsplash.com/photo-1531777319985-9dca28eeae64?w=900&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTF8fGdhbWUlMjBvZiUyMHRocm9uZXN8ZW58MHwwfDB8fHww')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        color: "white",
        padding: "40px 20px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        zIndex: 1,
        overflow: "hidden",
      }}
    >
      {/* Dark overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.7)",
          backdropFilter: "blur(6px)",
          zIndex: 0,
        }}
      />

      {/* Main Content */}
      <div
        style={{
          position: "relative",
          zIndex: 2,
          width: "100%",
          maxWidth: "80%",
          background: "rgba(255, 255, 255, 0.1)",
          border: "2px solid rgba(255, 255, 255, 0.2)",
          borderRadius: "20px",
          padding: "30px",
          boxShadow: "0 8px 32px 0 rgba( 31, 38, 135, 0.37 )",
          textAlign: "center",
        }}
      >
        <h1
          style={{
            fontSize: "2.5rem",
            marginBottom: "10px",
            fontWeight: "bold",
            textShadow: "2px 2px 4px #000000",
          }}
        >
          üêâ Welcome to the Throne Game üè∞
        </h1>
        <p
          style={{
            fontSize: "1.2rem",
            marginBottom: "30px",
            color: "#ccc",
            fontStyle: "italic",
          }}
        >
          Only the worthy may draw.
        </p>

        <DrawingBoard onSubmit={handleSubmit} />
        <PredictionDisplay prediction={prediction} />
      </div>
    </div>
  );
}

export default App;
